mapper.select:
select
, case c.brand
when 'ThinkPad' then '1' when 'ThinkCentre' then '1' when 'ThinkStation' then '1' when 'ThinkStation' then '1' when 'ThinkServer' then '1' when 'ThinkReality' then '1' else '0' end as category_type, case when eo.nbmc is not null then eo.nbmc else 0 end as enbmc, case when gsc1.nbmc is not null then gsc1.nbmc else 0 end as gnbmc, case when gsc2.nbmc is not null then (1-gsc2.nbmc/100)*c.one+eo.nbmc else case when gsc1.nbmc is not null then eo.nbmc + gsc1.nbmc else eo.nbmc end end as nbmc from margindb.cost_tape c
inner join margindb.cost_tape_family_mapping f on c.family = f.product_family and c.plant = f.plant
left join margindb.cost_tape_country_mapping_region m on c.country = m.country left join (select brand, subgeo, max(nbmc) as nbmc from margindb.cost_tape_eo group by brand, subgeo) eo on eo.brand = c.brand and eo.subgeo = c.subgeo left join (select `type`, brand, country, max(nbmc) as nbmc from margindb.cost_tape_gsc where `type` = 'US_DOLLAR' group by `type`, brand, country) gsc1 on gsc1.brand = c.brand and gsc1.country = c.country left join (select `type`, brand, country, max(nbmc) as nbmc from margindb.cost_tape_gsc where `type` = 'PERCENTAGE' group by `type`, brand, country) gsc2 on gsc2.brand = c.brand and gsc2.country = c.country
AND c.part_number = #{partNumber}
AND c.country = #{country}
AND m.region = #{region}

select 
from margindb.cost_tape c
inner join margindb.cost_tape_family_mapping f on c.family = f.product_family and c.plant = f.plant


    AND c.part_number = #{partNumber}


    AND c.country = #{country}


    AND c.plant = #{plant}


    AND c.family = #{productFamily}


    AND c.subgeo = #{subGeo}

select ,
case when left(c.part_number, 2) = 'ZA' then '1'
else '0' end as category_type
from margindb.cost_tape c


    AND c.part_number = #{partNumber}


    AND c.country = #{country}

select from margindb.cost_tape c inner join margindb.region_country_mapping m on c.subgeo = m.subgeo where data_type = '1'
AND c.country = #{country}
AND m.country = #{country}
union all
select from margindb.cost_tape c inner join margindb.region_country_mapping m on c.subgeo = m.subgeo where data_type = '1'
AND c.country = 'ALL'
AND m.country = #{country}
