mapper.select:
select
, case c.brand
when 'ThinkPad' then '1' when 'ThinkCentre' then '1' when 'ThinkStation' then '1' when 'ThinkStation' then '1' when 'ThinkServer' then '1' when 'ThinkReality' then '1' else '0' end as category_type, case when eo.nbmc is not null then eo.nbmc else 0 end as enbmc, case when gsc.nbmc is not null then gsc.nbmc else 0 end as gnbmc, eo.nbmc + gsc.nbmc as nbmc from margindb.cost_tape c
inner join margindb.cost_tape_family_mapping f on c.family = f.product_family and c.plant = f.plant
left join margindb.cost_tape_country_mapping_region m on c.country = m.country left join margindb.cost_tape_eo eo on eo.brand = c.brand and eo.subgeo = c.subgeo left join margindb.cost_tape_gsc gsc on gsc.brand = c.brand and gsc.family = c.family and eo.subgeo = c.subgeo
AND c.part_number = #{partNumber}
AND c.country = #{country}
AND m.region = #{region}

select 
from margindb.cost_tape c
inner join margindb.cost_tape_family_mapping f on c.family = f.product_family and c.plant = f.plant


    AND c.country = #{country}


    AND c.part_number = #{partNumber}


    AND c.plant = #{plant}


    AND c.family = #{productFamily}


    AND c.subgeo = #{subGeo}
